// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract DocumentHash {
    struct Record {
        uint256 mineTime;
        uint256 blockNumber;
        string  nomFile;
        string username;
    }

    mapping(bytes32 => Record) private docHashes;


    function addDocHash(bytes32  hash, string memory  _nomfile,string memory _username) public {
        Record memory newRecord = Record(block.timestamp, block.number, _nomfile, _username);
        docHashes[hash] = newRecord;
    }

    function findDocHash (bytes32 hash)
        public view
        returns (
            uint256,
            uint256,
            string memory,
            string memory
        )
    {
        return (
            docHashes[hash].mineTime,
            docHashes[hash].blockNumber,
            docHashes[hash].nomFile,
            docHashes[hash].username
        );
    }
}
